#!/bin/bash
#SBATCH --time=5:40:00
#SBATCH --mem=8GB
#SBATCH -p med
# Set number of nodes to run
#SBATCH --nodes=1
# Set number of tasks to run
#SBATCH --ntasks=1
# Set number of cores per task (default is 1)
#SBATCH --cpus-per-task=18
# Output and error files
#SBATCH -o job.%J.out

source activate Genomics

rm -r /work/mjuswilc/Domain/GreatTits/ultDeconvolution/Mixture/MethylCorreleations/GeneBody
mkdir /work/mjuswilc/Domain/GreatTits/ultDeconvolution/Mixture/MethylCorreleations/GeneBody
mkdir /work/mjuswilc/Domain/GreatTits/ultDeconvolution/Mixture/MethylCorreleations/GeneBody/Genes
mkdir /work/mjuswilc/Domain/GreatTits/ultDeconvolution/Mixture/MethylCorreleations/GeneBody/Chromosomes
mkdir /work/mjuswilc/Domain/GreatTits/ultDeconvolution/Mixture/MethylCorreleations/GeneBody/CorrMethBed
mkdir /work/mjuswilc/Domain/GreatTits/ultDeconvolution/Mixture/MethylCorreleations/GeneBody/Shared
mkdir /work/mjuswilc/Domain/GreatTits/ultDeconvolution/Mixture/MethylCorreleations/GeneBody/Out

#Annotating Gene Bodies
CHROMOSOMES=`awk '{print $1}' /work/mjuswilc/Domain/GreatTits/ultDeconvolution/Mixture/Bismark/MethylationBed/Genome/Abel-GCF_001522545.3_Parus_major1.1_genomic.chrom.genome`

zcat /work/mjuswilc/Domain/GreatTits/General/RawData/Abel-Reference/GCF_001522545.3_Parus_major1.1_genomic.gff.gz | awk 'BEGIN{OFS=FS="\t"}$3=="gene"{print $1 OFS $4 OFS $5 OFS $9}' | perl -pe 's#ID=(gene-[-\w]+);.*#$1#' > /work/mjuswilc/Domain/GreatTits/ultDeconvolution/Mixture/MethylCorreleations/GeneBody/Genes/Abel-GCF_001522545.3_Parus_major1.1_genomic-Gene.bed

for c in $CHROMOSOMES
do
grep "^$c" /work/mjuswilc/Domain/GreatTits/ultDeconvolution/Mixture/MethylCorreleations/GeneBody/Genes/Abel-GCF_001522545.3_Parus_major1.1_genomic-Gene.bed > /work/mjuswilc/Domain/GreatTits/ultDeconvolution/Mixture/MethylCorreleations/GeneBody/Chromosomes/$c-Abel-GCF_001522545.3_Parus_major1.1_genomic-Gene.bed &
JOBS=`jobs -l | grep -c '^\['`
while [ $JOBS -ge 18 ]
do
sleep 3s
JOBS=`jobs -l | grep -c '^\['`
done
done
wait


